import client from '../client'

function Post(props) {
  return (
    <article>
      <h1>{props.title}</h1>
      <BlockContent
        blocks={body}
        imageOptions={{ w: 320, h: 240, fit: 'max' }}
        {...client.config()}
      />
    </article>
  )
}

Post.getInitialProps = async function(context) {
  const { slug } = context.query
  return await client.fetch('*[_type == "historyArticle" && slug.current == $slug][0]', { slug })
}

export default Post
